import { useState, useEffect } from 'react'
import { supabase } from './supabaseClient'
import Signup from './Signup'
import Login from './Login'

function App() {
  const [user, setUser] = useState(null)
  const [showLogin, setShowLogin] = useState(false)

  useEffect(() => {
    // Check if user is already logged in
    const session = supabase.auth.getSession().then(({ data: { session } }) => {
      setUser(session?.user ?? null)
    })

    // Listen for login/signup/logout changes
    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null)
    })

    return () => listener?.subscription.unsubscribe()
  }, [])

  const handleLogout = async () => {
    await supabase.auth.signOut()
    setUser(null)
  }

  if (user) {
    return (
      <div className="min-h-screen flex flex-col justify-center items-center bg-green-100 text-center">
        <h1 className="text-2xl font-bold mb-4">ðŸŽ‰ Welcome, {user.email}!</h1>
        <p>This is your ADHD Routine Dashboard (coming soon!)</p>
        <button
          onClick={handleLogout}
          className="mt-6 bg-red-500 text-white px-4 py-2 rounded"
        >
          Log Out
        </button>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-purple-100 flex flex-col justify-center items-center">
      {showLogin ? <Login /> : <Signup />}
      <button
        onClick={() => setShowLogin(!showLogin)}
        className="mt-4 text-sm text-purple-700 underline"
      >
        {showLogin ? "Need to sign up?" : "Already have an account? Log in"}
      </button>
    </div>
  )
}

export default App
