import { useEffect, useState } from 'react'
import { supabase } from './supabaseClient'

export default function TaskDashboard({ user }) {
  const [tasks, setTasks] = useState([])
  const [newTask, setNewTask] = useState('')

  useEffect(() => {
    fetchTasks()
  }, [])

  async function fetchTasks() {
    const { data, error } = await supabase
      .from('tasks')
      .select('*')
      .eq('user_id', user.id)
      .order('created_at', { ascending: false })

    if (error) console.error(error)
    else setTasks(data)
  }

  async function addTask() {
    if (!newTask) return
    const { error } = await supabase.from('tasks').insert([
      {
        user_id: user.id,
        content: newTask,
        is_done: false,
      },
    ])
    if (error) console.error(error)
    else {
      setNewTask('')
      fetchTasks()
    }
  }

  async function toggleTaskDone(id, currentStatus) {
    const { error } = await supabase
      .from('tasks')
      .update({ is_done: !currentStatus })
      .eq('id', id)
    if (error) console.error(error)
    else fetchTasks()
  }

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">
        <span role="img" aria-label="brain">ðŸ§ </span> Your Routine
      </h2>

      <div className="flex mb-6">
        <input
          type="text"
          placeholder="Add a new task..."
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          className="flex-1 border p-2 rounded-l text-sm"
        />
        <button
          onClick={addTask}
          className="bg-purple-600 text-white px-4 rounded-r text-sm hover:bg-purple-700"
        >
          Add
        </button>
      </div>

      <ul>
        {tasks.map((task) => (
          <li
            key={task.id}
            className={`p-3 mb-3 rounded shadow flex justify-between items-center transition-all ${
              task.is_done ? 'bg-green-100 text-gray-600' : 'bg-white'
            }`}
          >
            <span
              className={`text-base ${
                task.is_done ? 'line-through italic' : 'font-medium'
              }`}
            >
              {task.is_done ? `âœ… ${task.content}` : task.content}
            </span>
            <button
              onClick={() => toggleTaskDone(task.id, task.is_done)}
              className="text-xs text-purple-600 underline hover:text-purple-800"
            >
              {task.is_done ? 'Undo' : 'Done'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  )
}
